package com.example.rishabh.favouritetoys;import android.support.v7.app.AppCompatActivity;import android.os.Bundle;import android.widget.TextView;public class MainActivity extends AppCompatActivity {    TextView myToysText;    String[] myToys;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_main);        myToysText=(TextView)findViewById(R.id.toysText);        myToys=ToyBox.getToys();        for (int i=0;i<myToys.length;i++)        {            myToysText.append(myToys[i]+"\n\n\n");        }    }}